mapper.select:
SELECT Y.* FROM ( SELECT ROWNUM NUM, X.* FROM ( SELECT REG_SN, TTL, CLSF_NO, FILE_SN, RGTR_NM, INQ_CNT, RLS_YN, ANSWER_AT, TO_CHAR(A.REG_DT, 'YYYY-MM-DD') AS REG_DT, (SELECT COUNT(FILE_SN) FROM TN_FILE WHERE FILE_SN = A.FILE_SN) AS FILE_CNT, 0 AS COMM_CNT FROM TN_SMPLE A
ORDER BY ${paramMap.q_sortName} ${paramMap.q_sortOrder}
ORDER BY A.REG_SN DESC
) X WHERE ROWNUM <= #{paramMap.q_pagingEndNum} ) Y WHERE NUM >= #{paramMap.q_pagingStartNum}

mapper.delete:
DELETE FROM TN_SMPLE_ANSWER
WHERE
        REG_SN = #{paramMap.q_regSn}
    AND ANSWER_NO = #{paramMap.q_answerNo}

mapper.select:
SELECT Y.* FROM ( SELECT ROWNUM NUM, X.* FROM ( SELECT REG_SN, TTL, CLSF_NO, RGTR_NM, INQ_CNT, RLS_YN, TO_CHAR(A.REG_DT, 'YYYY-MM-DD') AS REG_DT FROM TN_SMPLE A
ORDER BY ${paramMap.q_sortName} ${paramMap.q_sortOrder}
ORDER BY A.REG_SN DESC
) X WHERE ROWNUM <= #{paramMap.q_pagingEndNum} ) Y WHERE NUM >= #{paramMap.q_pagingStartNum}
