mapper.select:
SELECT
A.BBS_SN, A.BBS_DOC_NO, A.OPNN_SN, A.OPNN_RFRNC_SN, A.SORT_SN, A.OPNN_GRD_SN, A.OPNN_CN, A.DCLR_CNT, A.RCMDTN_CNT, A.DERCM_CNT, A.IP_ADDR, A.PSWD, A.RGTR_NM, A.RGTR_ID, A.REG_DT, A.DEL_YN, A.DEL_DT, A.PIC_ID, A.PIC_DEL_YN, A.PIC_DEL_DT,
FROM TN_BBS_CMNT A WHERE A.BBS_SN = #{bbsSn} AND A.BBS_DOC_NO = #{bbsDocNo} AND A.OPNN_SN = #{opnnSn}

mapper.insert:
SELECT NVL(MAX(OPNN_SN), 0) + 1 FROM TN_BBS_CMNT WHERE BBS_SN = #{bbsSn} AND BBS_DOC_NO = #{bbsDocNo}
INSERT INTO TN_BBS_CMNT (
BBS_SN, BBS_DOC_NO, OPNN_SN, OPNN_RFRNC_SN, SORT_SN, OPNN_GRD_SN, OPNN_CN, DCLR_CNT, RCMDTN_CNT, DERCM_CNT, IP_ADDR, PSWD, RGTR_NM, RGTR_ID, REG_DT
) VALUES (
#{bbsSn}, #{bbsDocNo}, #{opnnSn},
#{opnnRfrncSn},
#{opnnSn},
#{sortSn}, #{opnnGrdSn}, #{opnnCn}, 0, 0, 0, #{ipAddr}, #{pswd}, #{rgtrNm}, #{rgtrId}, SYSDATETIME
)

mapper.select:
SELECT

    COUNT(1) AS CNT

FROM
    TN_BBS_CMNT A
WHERE
         A.BBS_SN = #{bbsSn}
    AND A.BBS_DOC_NO = #{bbsDocNo}
    AND A.OPNN_RFRNC_SN = #{opnnSn}
    AND A.OPNN_SN != A.OPNN_RFRNC_SN

mapper.update:
UPDATE TN_BBS_CMNT 



            DCLR_CNT = DCLR_CNT +1


            RCMDTN_CNT = RCMDTN_CNT + 1


            DERCM_CNT = DERCM_CNT + 1



WHERE
     BBS_SN = #{bbsSn}
    AND BBS_DOC_NO = #{bbsDocNo}
    AND OPNN_SN = #{opnnSn}

UPDATE TN_BBS_CMNT

    SORT_SN = SORT_SN + 1

WHERE
         BBS_SN = #{bbsSn}
    AND BBS_DOC_NO = #{bbsDocNo}
    AND OPNN_RFRNC_SN = #{opnnRfrncSn}
    AND SORT_SN >= #{sortSn}

UPDATE TN_BBS_CMNT 

    OPNN_CN = #{opnnCn},
    DEL_YN = #{delYn},
    DEL_DT = SYSDATETIME

WHERE
     BBS_SN = #{bbsSn}
    AND BBS_DOC_NO = #{bbsDocNo}
    AND OPNN_SN = #{opnnSn}

mapper.delete:
DELETE FROM TN_BBS_CMNT
WHERE
     BBS_SN = #{bbsSn}
    AND BBS_DOC_NO = #{bbsDocNo}
    AND OPNN_SN = #{opnnSn}

DELETE FROM TN_BBS_CMNT
WHERE
     BBS_SN = #{bbsSn}
    AND BBS_DOC_NO = #{bbsDocNo}
