mapper.select:
SELECT A.BBS_STNG_SN, A.COL_ID, A.COL_NM, A.SCRN_NM, A.COL_TYPE_NM, A.BBS_COL_EXPLN, A.SRCH_TYPE, A.ESNTL_YN, A.SORT_SN, A.LST_INDCT_YN, A.INQ_INDCT_YN, A.INPT_INDCT_YN FROM TN_BBS_ITEM_SETUP A, TN_BBS_SETUP B WHERE A.BBS_STNG_SN = B.BBS_STNG_SN AND B.BBS_SN = #{bbsSn}
AND SRCH_TYPE != ''
ORDER BY A.SORT_SN ASC
