mapper.select:
SELECT
A.REG_SN, A.TTL, A.DOC_CONTS_CN, A.FILE_SN, A.SE_CD_ID, A.SCHDL_SE_CD_ID, A.HLDY_SE_CD_ID, A.PLC_CD_ID, TO_CHAR(TO_DATE(A.BGNG_YMD , 'YYYYMMDD'), 'YYYY-MM-DD') AS BGNG_YMD, TO_CHAR(TO_DATE(A.END_YMD , 'YYYYMMDD'), 'YYYY-MM-DD') AS END_YMD, A.BGNG_HR, A.END_HR, A.RPTT_YN, A.RPTT_TYPE_CD, TO_CHAR(TO_DATE(A.RPTT_END_YMD , 'YYYYMMDD'), 'YYYY-MM-DD') AS RPTT_END_YMD, A.MNDAY_RSVT_NO, A.TSDAY_RSVT_NO, A.WDDAY_RSVT_NO, A.TRDAY_RSVT_NO, A.FRDAY_RSVT_NO, A.STDAY_RSVT_NO, A.SNDAY_RSVT_NO, A.COLOR_NM, A.RLS_YN, A.RGTR_ID, A.REG_DT, A.MDFR_ID, A.UPDT_DT, (SELECT CD_NM FROM TC_CODE WHERE HGHRK_CD_ID = #{hghrkCdId} AND CD_ID = A.SE_CD_ID) SE_NM, (SELECT CD_NM FROM TC_CODE WHERE HGHRK_CD_ID = #{hghrkCdId} AND CD_ID = A.SCHDL_SE_CD_ID) SCHDUL_SE_NM, (SELECT CD_NM FROM TC_CODE WHERE HGHRK_CD_ID = #{hghrkCdId} AND CD_ID = A.HLDY_SE_CD_ID) RESTDE_SE_NM, (SELECT CD_NM FROM TC_CODE WHERE HGHRK_CD_ID = #{hghrkCdId} AND CD_ID = A.PLC_CD_ID) PLACE_NM, (SELECT PIC_NM FROM TN_MNGR WHERE PIC_ID = A.RGTR_ID) AS RGTR_NM, (SELECT PIC_NM FROM TN_MNGR WHERE PIC_ID = A.MDFR_ID) AS UPDUSR_NM,
FROM TN_SCHDUL A WHERE A.REG_SN = #{paramMap.q_regSn}

sql.where:
A.REG_SN = B.REG_SN

AND A.RLS_YN = #{paramMap.q_rlsYn}


AND A.REG_SN = #{paramMap.q_regSn}


AND B.BGNG_YMD >= REPLACE(#{paramMap.q_bgngYmd}, '-', '')


AND B.END_YMD <= REPLACE(#{paramMap.q_endYmd}, '-', '')


AND A.TTL LIKE '%' || #{paramMap.q_searchVal} || '%'


AND A.SE_CD_ID = #{paramMap.q_seCdId}


AND A.SCHDL_SE_CD_ID = #{paramMap.q_schdlSeCdId}


AND A.HLDY_SE_CD_ID = #{paramMap.q_hldySeCdId}


AND A.PLC_CD_ID = #{paramMap.q_plcCdId}
